import {getItemsInfo, getPeopleInfo} from "../../utils/api";
import {allItems} from "./actions";
import {ALL_ITEMS} from "./constants";


const initialState = {
    items:[]
};

const MainReducer = (state = initialState, action) => {
    switch (action.type) {
        case ALL_ITEMS:
            return {
                ...state,
                items: action.payload,
            };

        default:
            return state;
    }
};
export const getItems = (resource) => dispatch => {
    console.log(resource)
    getItemsInfo(resource)
        .then( item => {
            dispatch(allItems(item.data.results));
        })
}
export default MainReducer;
