/* eslint-disable */
import React from "react";
import { useDispatch } from "react-redux";
import { useHistory } from 'react-router-dom'
import { Field, reduxForm } from 'redux-form'
import {email, password, required} from "../../utils/helpers/validators";
import {Input} from "../../utils/helpers/formControls";
import {RegisterRequest} from "../../redux/session/AuthReducer";
import Button from "../../generic/Button";

const RegisterForm = (props) => {
    return (
        <form className="register-form" onSubmit={props.handleSubmit}>
            <div>
                <Field placeholder={'register'} name={"register"} component={Input}
                       validate={ [email, required] }
                />
            </div>
            <div>
                <Field placeholder={'password'} name={"password"} component={Input}
                       validate={[password, required]}
                       type="password"/>
            </div>
            <div>
                <Button text="Submit" />
            </div>
        </form>
    )
}

const RegisterReduxForm = reduxForm ({ form: 'register'})(RegisterForm)

const Register = ({ setState }) => {
    let dispatch = useDispatch();
    const onSubmit = (formData) => {
        RegisterRequest(formData)(dispatch);
    }
    return (
        <div className="registerFormWrapper">
            <Button text="Register" />
            <RegisterReduxForm  onSubmit={onSubmit}/>
        </div>
    );
};





export default Register;
