import {takeEvery, put, call} from 'redux-saga/effects'
import {allItems, loading} from "./Layout/LayoutActions";
import {ALL_ITEMS, ALL_ITEMS_CREATOR} from "./Layout/LayoutConstants";
import {getItemsInfo, login, register} from "../../utils/api";
import {error, success} from "../../utils/helpers/infoMessages";
import {redirect} from "../../utils/helpers/functions";
import {loginAction} from "./Auth/AuthActions";
import {LOADING, LOGIN, LOGIN_CREATOR, REGISTER, REGISTER_CREATOR} from "./Auth/AuthConstants";

export function* sagaWatcher () {
    yield takeEvery(ALL_ITEMS_CREATOR, getItemsWorker)
    yield takeEvery(REGISTER_CREATOR, registerWorker)
}

function* getItemsWorker(data) {
    const {resource, page} = data.payload
    const items = yield call(getItemsInfo, {resource, page})
    console.log(items.data.results)
    yield put({type: ALL_ITEMS, payload: items.data.results})
    // dispatch(allItems({resource, page}))
}

function* registerWorker(data) {
    const {formData, history} = data.payload
    yield put({REGISTER, payload: formData})
    const items = yield call(register, {formData})
    console.log(items)
        //     if (response.statusText === "OK") {
