import React from "react";
import { Button } from "antd";
import { useHistory } from 'react-router-dom'
import { Field, reduxForm } from 'redux-form'
import {Input} from "../../utils/helpers/formControls";
import {email, required, password} from "../../utils/helpers/validators";

const RegisterForm = () => {

    return (
        <form className="login-form">
            <div>
                <Field placeholder={'login'} name={"login"} component={Input}
                       validate={ [email, required] }/>
            </div>
            <div>
                <Field placeholder={'password'} name={"password"} component={Input}
                       validate={[password, required]}
                       type="password"/>
            </div>
            <div>
                <button>Submit</button>
            </div>
        </form>
    );
};
const LoginReduxForm = reduxForm ({ form: 'login'})(RegisterForm)

const Register = ({ setState }) => {

    const onSubmit = (formData) => {
        console.log(12, formData)
    }
    return (
        <div className="root-ParticipantRegister">
            <div className="participant-register__form-wrapper">
                <div className="participant-heading">V-RAP: Participant</div>
                <div className="participant-register__login-btns">
                    <Button
                        className="participant-register__research-btn"
                        onClick={() => setState(false)}
                    >
                        Login
                    </Button>
                    <LoginReduxForm onSubmit={onSubmit}/>
                </div>
            </div>
        </div>
    );
};

export default Register;
