import {ITEMS, LOADING, MORE_ITEMS} from "./LayoutConstants";


const initialState = {
    items:[],
    loadItems: [],
    isLoading: false
};

const LayoutReducer = (state = initialState, action) => {
    switch (action.type) {
        case ITEMS:
            return {
                ...state,
                items: action.payload
            };
        case MORE_ITEMS:
            return {
                ...state,
                items: state.items.concat(action.payload)
            };
        case LOADING:
            return {
                ...state,
                isLoading: action.payload,
            };
        default:
            return state;
    }
};
export default LayoutReducer;
