import {ALL_ITEMS, LOADING, PEOPLE, PLANETS, RESOURCE, STARSHIPS} from "./LayoutConstants";
import {getItemsInfo} from "../../../utils/api";
import {allItems, expectedResource, peopleItems, planetsItems, starshipsItems} from "./LayoutActions";
import {error} from "../../../utils/helpers/infoMessages";


const initialState = {
    items:[],
    people: [],
    planets:[],
    starships:[],
    isLoading: false,
    resource: ''
};

const LayoutReducer = (state = initialState, action) => {
    switch (action.type) {
        case ALL_ITEMS:
            return {
                ...state,
                items: state.items.concat(action.payload)
            };
        case PEOPLE:
            return {
                ...state,
                people: state.people.concat(action.payload)
            };
        case PLANETS:
            return {
                ...state,
                planets: state.planets.concat(action.payload)
            };
        case STARSHIPS:
            console.log(action.payload)
            return {
                ...state,
                starships: state.starships.concat(action.payload)
            };
        case RESOURCE:
            return {
                ...state,
                resource: action.payload,
            };
        case LOADING:
            return {
                ...state,
                isLoading: action.payload,
            };

        default:
            return state;
    }
};
export const getItems = (resource, page) => dispatch => {
    isLoading(true)
    getItemsInfo(resource, page)
        .then( item => {
            if (resource === 'people') {
                dispatch(peopleItems(item.data.results));
            }
            if (resource === 'planets') {
                dispatch(planetsItems(item.data.results));
            }
            if (resource === 'starships') {
                dispatch(starshipsItems(item.data.results));
            }

            dispatch(allItems(item.data.results));
        })
        .catch(() => {
            error('Something is wrong')
        })
}
export const setResource = resource => dispatch => {
            dispatch(expectedResource(resource));
}
export default LayoutReducer;
