import {getItemsInfo, getPeopleInfo} from "../../utils/api";
import {allItems, expectedResource} from "./actions";
import {ALL_ITEMS, RESOURCE} from "./constants";


const initialState = {
    items:[],
    resource: ''
};

const MainReducer = (state = initialState, action) => {
    switch (action.type) {
        case ALL_ITEMS:
            console.log(state.items, action.payload)
            return {
                ...state,
                items: state.items.concat(action.payload)
            };
        case RESOURCE:
            console.log(action.payload)
            return {
                ...state,
                resource: action.payload,
            };

        default:
            return state;
    }
};
export const getItems = (resource, page) => dispatch => {
    getItemsInfo(resource, page)
        .then( item => {
            console.log(12, item.data.results)
            dispatch(allItems(item.data.results));
        })
}
export const setResource = resource => dispatch => {
            dispatch(expectedResource(resource));
}
export default MainReducer;
