import React, {useState, useEffect} from 'react';
import { Layout, Menu} from 'antd';
import './Layout.scss'
import {useDispatch, useSelector} from "react-redux";
import ItemCard from "./ItemCard/ItemCard";
import Button from "../../generic/Button";
import {Link} from "react-router-dom";
import {getItems} from "../../redux/session/Layout/LayoutReducer";
import {allItems, peopleItems, planetsItems, starshipsItems} from "../../redux/session/Layout/LayoutActions";
import {getItemsInfo} from "../../utils/api";
import {loader} from "../../utils/helpers/Loader";
import sagaWorker from '../../redux/session/Layout/LayoutReducer'
import {ALL_ITEMS} from "../../redux/session/Layout/LayoutConstants";

const { Header, Content, Footer, Sider } = Layout;

const MainLayout = ({match}) => {
    match.path && match.path !== '/' &&
    localStorage.setItem('resource', match.path);

    let localResource = localStorage.getItem('resource');
    if (!localResource)
        localStorage.setItem('resource', 'people');

    const isLoading = useSelector(state => state.main.isLoading)
    const itemss = useSelector(state => state.main.items)

    console.log(12, itemss)

    const [resource, setResource] = useState(localResource.slice(1))
    const [collapsed, setCollapsed] = useState(false)
    const [people, setPeople] = useState([])
    const [planets, setPlanets] = useState([])
    const [starships, setStarships] = useState([])
    const [page, setPage] = useState(1)
    const dispatch = useDispatch();

    useEffect(() => {
        dispatch(allItems({resource, page}))
        // dispatch({type:ALL_ITEMS, payload: {resource, page}})
        // sagaWorker(resource, page)
        // getItemsInfo(resource, page)
        //     .then( item => {
        //         if (resource === 'people') {
        //             setPeople(prevState => prevState.concat(item.data.results));
        //         }
        //         if (resource === 'planets') {
        //             setPlanets(prevState => prevState.concat(item.data.results));
        //         }
        //         if (resource === 'starships') {
        //             setStarships(prevState => prevState.concat(item.data.results));
        //         }
        //
        //         dispatch(allItems(item.data.results));
        //     })
    }, [resource, page])
    let el = [], items=[]
    if (resource === 'people') {
        items=el.concat(people)
    }
    if (resource === 'planets') {
        items=el.concat(planets)
    }
    if (resource === 'starships') {
        items=el.concat(starships)
    }

    console.log(people)
    const changeResource = value => {
        document.documentElement.scrollTop=0;
        if (value === 'people') {
            setStarships([])
            setPlanets([])
        }
        if (value === 'planets') {
            setStarships([])
            setPeople([])
        }
        else {
            setPlanets([])
            setPeople([])
        }

        setResource(value)
        setPage(1)

        localStorage.setItem('resource', value);
    }
    const onCollapse = () => setCollapsed(prevState => !prevState)
    const onLoadButtonClick = () => setPage(page+1)

    return (
        <Layout style={{ minHeight: '100vh' }}>
            <Sider collapsible collapsed={collapsed} onCollapse={onCollapse}>
                <div className="logo" />
                <Menu className="menu" theme="dark" defaultSelectedKeys={['1']} mode="inline" >
                        <Menu.Item className={resource==='people' ? 'activeTab' : 'noActiveTab'} key="1" onClick={() => changeResource('people')}>
                            <Link to='people'>People</Link>
                        </Menu.Item>
                        <Menu.Item className={resource==='planets' ? 'activeTab' : 'noActiveTab'} key="2" onClick={() => changeResource('planets')}>
                            <Link to='planets'> Planets</Link>
                        </Menu.Item>


                        <Menu.Item className={resource==='starships' ? 'activeTab' : 'noActiveTab'} key="3" onClick={() => changeResource('starships')}>
                            <Link to='starships'> Starships</Link>
                        </Menu.Item>
                </Menu>
            </Sider>
            <Layout className="site-layout">
                <Header className="site-layout-background"
                        style={{ padding: 0, color: '#fff', textAlign: 'center' }}
                >
                    React APP by Roman
                </Header>
                <Content style={{ margin: '0 16px' }}>
                    {
                        isLoading
                            ?
                                loader
                            :
                        <div className="site-layout-background" style={{ padding: 24, minHeight: 360 }}>
                            <div className="content-wrapper">
                                {
                                    items?.length > 0 && items.map(item => {
                                        return <ItemCard
                                            item={item}
                                            resource={resource}
                                            key={item.url}
                                        />
                                    })
                                }
                                <Button text="Load more" funk={onLoadButtonClick}/>
                            </div>

                        </div>
                    }
                </Content>
                <Footer style={{ textAlign: 'center' }}>Roman Demianchuk, 2021</Footer>
            </Layout>
        </Layout>

    );
}

export default MainLayout;
