import {takeEvery, put, call} from 'redux-saga/effects'
import {allItems} from "./Layout/LayoutActions";
import {ALL_ITEMS} from "./Layout/LayoutConstants";
import {getItemsInfo} from "../../utils/api";

export function* sagaWatcher () {
    yield takeEvery(ALL_ITEMS, getItemsWorker)
}

function* getItemsWorker(data) {
    const {resource, page} = data.payload
    console.log(data)
    const items = yield call(getItemsInfo, {resource, page})
    console.log(items)
    yield put({type: ALL_ITEMS, items: items})
    // dispatch(allItems({resource, page}))
}
