/* eslint-disable */
import {login, register} from "../../utils/api";
import {LOGIN, REGISTER} from "./constants";
import {loginAction} from "./actions";
import {success, error} from "../../utils/helpers/infoMessages";
import {useHistory} from "react-router-dom";
import {redirect} from "../../utils/helpers/functions";

const initialState = {
    loginInfo:[],
    isAuth: false
};


const AuthReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN:
            return {
                ...state,
                loginInfo: action.payload,
                isAuth: true,
            };
        case REGISTER:
            return {
                ...state,
                // registerData: action.payload,
            };

        default:
            return state;
    }
};
export default AuthReducer;

export const LoginRequest = (data, history) => (dispatch) => {
    login(data)
        .then((response) => {
            if (response.statusText === "OK") {
                let token = response.data.token;
                localStorage.setItem("userLoginToken", token);
                localStorage.setItem("isAuth", true);
                success('You successfully login')
                redirect('/people', history)
                dispatch(loginAction(response));
            }
        })
        .catch((e) => error(e.response.data.message))

};

export const RegisterRequest = (data, history) => (dispatch) => {
    register(data)
        .then((response) => {
            console.log(45, response)
            if(response.request.statusText === "Created") {
                login(data)
                    .then((response) => {
                        console.log(45, response)
                        if (response.statusText === "OK") {
                            let token = response.data.token;
                            localStorage.setItem("userLoginToken", token);
                            localStorage.setItem("isAuth", true);
                            dispatch(loginAction(response));
                            success('You successfully register and login')
                            redirect('/people', history)
                        }
                    })
            }
        })
        .catch((e) => error(e.response.data.message))
};
