import {getPeopleInfo} from "../../utils/api";
import {allPeople} from "./actions";
import {ALL_PEOPLE} from "./constants";


const initialState = {
    people:[]
};

const MainReducer = (state = initialState, action) => {
    switch (action.type) {
        case ALL_PEOPLE:
            return {
                ...state,
                people: action.payload,
            };

        default:
            return state;
    }
};
export const getPeople = () => dispatch => {
    getPeopleInfo()
        .then( item => {
            dispatch(allPeople(item.data.results));
        })
}
export default MainReducer;
