/* eslint-disable */
import React from "react";
import {useDispatch, useSelector} from "react-redux";
import {Link, useHistory} from 'react-router-dom'
import { Field, reduxForm } from 'redux-form'
import {email, password, required} from "../../utils/helpers/validators";
import {Input} from "../../utils/helpers/formControls";
import Button from "../../generic/Button";
import './Register.scss'
import {REGISTER_CREATOR} from "../../redux/session/Auth/AuthConstants";
import {loader} from "../../utils/helpers/loader";
import {getFromStorage} from "../../utils/helpers/functions";
import {Form} from "../../utils/helpers/form";

const RegisterForm = (props) => {
    return <Form handleSubmit={props.handleSubmit}/>
}

const RegisterReduxForm = reduxForm ({ form: 'login'})(RegisterForm)

const Register = () => {
    const history = useHistory();
    const isAuth = getFromStorage("isAuth");

    const {isLoading} = useSelector(state => state.main)
    isAuth && history.push('/people')
    let dispatch = useDispatch();
    const onSubmit = (formData) => dispatch({type:REGISTER_CREATOR, payload: {formData, history}})
    return (
        <div className="registerFormWrapper">
            {isLoading ?
            loader() :
                <>
                    <Link to={'/login'}>to login</Link>
                    <RegisterReduxForm  onSubmit={onSubmit}/>
                </>
            }
        </div>
    );
};





export default Register;
