import {applyMiddleware, createStore, combineReducers, compose} from "redux";
import createSagaMiddleWare from 'redux-saga'
import MainReducer from '../redux/session/MainReducer'
import AuthReducer from "../redux/session/AuthReducer";
import {sagaWatcher} from "../redux/session/Sagas";
import {reducer as formReducer} from 'redux-form'

const sagaMiddleware  = createSagaMiddleWare()
const composeEnhancers = window.__REDUX_DEVTOOLS_EXTENSION__ || compose;





const allReducers = combineReducers({
    main: MainReducer,
    auth: AuthReducer,
    form: formReducer
});
const store = createStore(allReducers, composeEnhancers(applyMiddleware(sagaMiddleware)));
sagaMiddleware.run(sagas);


export default store;
