import {ALL_ITEMS, RESOURCE} from "./LayoutConstants";
import {getItemsInfo} from "../../../utils/api";
import {allItems, expectedResource} from "./LayoutActions";


const initialState = {
    items:[],
    people: [],
    planets:[],
    starships:[],
    resource: ''
};

const LayoutReducer = (state = initialState, action) => {
    switch (action.type) {
        case ALL_ITEMS:
            return {
                ...state,
                items: state.items.concat(action.payload)
            };
        case RESOURCE:
            return {
                ...state,
                resource: action.payload,
            };

        default:
            return state;
    }
};
export const getItems = (resource, page) => dispatch => {
    getItemsInfo(resource, page)
        .then( item => {
