import {ITEMS, LOADING, MORE_ITEMS, PEOPLE, PLANETS, RESOURCE, STARSHIPS} from "./LayoutConstants";
import {expectedResource} from "./LayoutActions";


const initialState = {
    items:[],
    loadItems: [],
    people: [],
    planets:[],
    starships:[],
    isLoading: false,
    resource: ''
};

const LayoutReducer = (state = initialState, action) => {
    switch (action.type) {
        case ITEMS:
            return {
                ...state,
                items: action.payload
            };
        case MORE_ITEMS:
            return {
                ...state,
                items: state.items.concat(action.payload)
            };

        case PEOPLE:
            return {
                ...state,
                people: state.people.concat(action.payload)
            };
        case PLANETS:
            return {
                ...state,
                planets: state.planets.concat(action.payload)
            };
        case STARSHIPS:
            console.log(action.payload)
            return {
                ...state,
                starships: state.starships.concat(action.payload)
            };
        case RESOURCE:
            return {
                ...state,
                resource: action.payload,
            };
        case LOADING:
            return {
                ...state,
                isLoading: action.payload,
            };

        default:
            return state;
    }
};
