import {LOGIN, REGISTER} from "./AuthConstants";
import {LOADING} from "../Layout/LayoutConstants";


const initialState = {
    loginInfo:[],
    isAuth: false,
    isLoading: false,
};


const AuthReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOGIN:
            return {
                ...state,
                loginInfo: action.payload.data,
                isAuth: true,
            };

        case LOADING:
            return {
                ...state,
                isLoading: action.payload,
            };
        default:
            return state;
    }
};
export default AuthReducer;
